import React, { useEffect } from "react";
import { Formik, Field } from "formik";
import { Form, Button } from "react-bootstrap";
import * as yup from "yup";
import { Link } from "react-router-dom";

import { userLogin } from "./Redux/actions";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

const sleep = (ms) => new Promise((r) => setTimeout(r, ms));

const INTIALVALUES = {
  email: "",
  password: ""
};

const SCHEMA = yup.object().shape({
  username: yup.string().required("Username Required"),
  password: yup.string().required("Password Required")
});

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userLoginR = useSelector((state) => state.userLoginReducer);
  const { error, userInfo } = userLoginR;

  const handleStudentLogin = async (values) => {
    console.log("logining");
    await sleep(500);
    alert(JSON.stringify(values, null, 2));
    dispatch(userLogin(values));
  };

  useEffect(() => {
    if (userInfo && !userInfo.is_first) {
      navigate("/student/home");
    } else if (userInfo && userInfo.is_first) {
      navigate("/intro");
    }
  }, [userInfo, navigate]);

  return (
    <div className="py-5 d-flex justify-content-center">
      <div className="w-25 p-3 border border-dark rounded">
        <h1 className="d-flex justify-content-center"> Login </h1>
        <Formik
          initialValues={INTIALVALUES}
          onSubmit={handleStudentLogin}
          validationSchema={SCHEMA}
        >
          {({
            isSubmitting,
            handleSubmit,
            handleChange,
            values,
            touched,
            errors
          }) => (
            <Form>
              <Form.Label>Email</Form.Label>
              <Form.Group className="mb-5" controlId="email">
                <Form.Control
                  type="text"
                  name="username"
                  placeholder="Email"
                  value={values.username}
                  onChange={handleChange}
                  isInvalid={!!errors.username}
                  isValid={touched.username && !errors.username}
                  feedback={errors.username}
                  className="form-field"
                />
                {errors.username && (
                  <Form.Control.Feedback type="invalid">
                    {errors.username}
                  </Form.Control.Feedback>
                )}
                <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
              </Form.Group>

              <Form.Label>Password</Form.Label>
              <Form.Group className="mb-2" controlId="password">
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={values.password}
                  onChange={handleChange}
                  isInvalid={!!errors.password}
                  isValid={touched.password && !errors.password}
                  feedback={errors.password}
                />
                {errors.password && (
                  <Form.Control.Feedback type="invalid">
                    {errors.password}
                  </Form.Control.Feedback>
                )}
                <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
              </Form.Group>

              <div className="d-flex justify-content-end mb-5">
                <Link to="/forget" className="text-end text-muted">
                  Forget password?
                </Link>
              </div>

              <div className="d-grid gap-2 justify-content-center">
                <Button
                  type="submit"
                  className="my-2 login-btn"
                  text="Login"
                  variant="dark"
                >
                  Login
                </Button>
              </div>

              <div className="text-center text-wrap">
                By engaging in our services, you have fully agreed to our Terms
                of Service and have given written consent to our Data Notice.
              </div>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
};
export default Login;
